#!/bin/bash
set -e

if [ ! -d /var/rubedo_sources ]; then
    mkdir -p /root/.ssh
    echo "github.com,192.30.252.131 ssh-rsa " > /root/.ssh/known_hosts
    chmod 644 /root/.ssh/known_hosts
    git clone -b 3.1.x https://github.com/WebTales/rubedo.git /var/www/html/rubedo
else
    cp -R /var/rubedo_sources /var/www/html/rubedo
fi

exec "$@"